        }

        const url ='https://hooks.slack.com/services/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'

        fetch(url, {
            method:'POST',
            body:JSON.stringify(payload)
        }).then(() => {
            alert('送信が完了しました。迫ってご連絡します')
            this.setState({
                name: "",
                email: "",
                description:""
            })
            return props.handleClose();
        })
    }


